/// This is the main file

import React, {Component} from 'react'
import Web3 from 'web3'
import './App.css'
import NavBar from './Navbar'
import Tether from '../truffle_abis/Tether.json'


class App extends Component {

    // call loadWeb3 function before anoything else "Way to conncet your wallet to app"
    async UNSAFE_componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockData() // reads crypto wallet account
    }

    async loadWeb3() {
        if (window.ethereum) {
            // make a new instance of web3, wait and enable it
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        } else if(window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        } else {
            window.alert('404! Check our MetaMask!!')

        }
    }

    async loadBlockData() {
        const web3 = window.web3
        const userWallet = await web3.eth.getAccounts()
        this.setState({accounts: userWallet[0]})
        console.log(userWallet)
        const networkId = await web3.eth.net.getId()


        //load tether contract
        const tetherData = Tether.networks[networkId]
        if (tetherData) {
            const tether = new web3.eth.Contract(Tether.abi, tetherData.address)
            this.setState(tether)
            let tetherBalance = tether.methods.balanceOf(this.state.account).call()
            this.setState({tetherBalance: tetherBalance.toString()}) 
            console.log(tetherBalance)
        } else {
            window.alert("Tether contract not deployed!")
        }
    }

    constructor(props) {
        super(props)
        this.state = {
            account: '0x0',
            tether: {},
            rwd: {},
            decentralBank: {},
            tetherBalance: '0',
            rwdBalance: '0',
            stakingBalance: '0',
            loading: true

        }
    }

    render() {
        return (
            <div>
                <NavBar account = {this.state.account} />
                    <div className='text-center'>
                        <h1></h1>
                    </div>
            </div> 
        )
    }

}

export default App;
